<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Kushma Art Project</title>
    <link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/6.4.0/css/all.min.css">
    <style>
        /* Global Styles */
        * {
            margin: 0;
            padding: 0;
            box-sizing: border-box;
            font-family: 'Helvetica Neue', Arial, sans-serif;
        }

        body {
            background-color: #f8f8f8;
            color: #333;
            overflow-x: hidden;
        }

        /* Typography */
        h1, h2, h3 {
            font-weight: 700;
            text-transform: uppercase;
            letter-spacing: 2px;
        }

        /* Navigation */
        nav {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            padding: 20px;
            z-index: 100;
            display: flex;
            justify-content: space-between;
            align-items: center;
            background-color: rgba(0,0,0,0.7);
            backdrop-filter: blur(10px);
            transition: all 0.3s ease;
        }

        nav.scrolled {
            padding: 10px 20px;
            background-color: rgba(0,0,0,0.9);
        }

        .logo {
            font-size: 1.8rem;
            font-weight: 900;
            color: white;
            text-decoration: none;
        }

        .nav-links {
            display: flex;
            gap: 30px;
        }

        .nav-links a {
            color: white;
            text-decoration: none;
            font-weight: 500;
            transition: all 0.3s ease;
        }

        .nav-links a:hover {
            color: #f0c14b;
        }

        /* Hero Section - Matching Given Design */
        .hero {
            height: 100vh;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            text-align: center;
            position: relative;
            overflow: hidden;
            background-color: #000;
        }

        .hero-video {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            object-fit: cover;
            z-index: 1;
            opacity: 0.7;
        }

        .hero-content {
            position: relative;
            z-index: 2;
            color: white;
            max-width: 800px;
            padding: 0 20px;
        }

        .hero h1 {
            font-size: 5rem;
            margin-bottom: 1rem;
            line-height: 1;
        }

        .hero h2 {
            font-size: 1.5rem;
            margin-bottom: 2rem;
            font-weight: 300;
        }

        .hero p {
            font-size: 1.2rem;
            margin-bottom: 3rem;
            line-height: 1.6;
        }

        .scroll-down {
            position: absolute;
            bottom: 30px;
            left: 50%;
            transform: translateX(-50%);
            color: white;
            font-size: 2rem;
            animation: bounce 2s infinite;
            cursor: pointer;
            z-index: 3;
        }

        @keyframes bounce {
            0%, 20%, 50%, 80%, 100% {
                transform: translateY(0) translateX(-50%);
            }
            40% {
                transform: translateY(-20px) translateX(-50%);
            }
            60% {
                transform: translateY(-10px) translateX(-50%);
            }
        }

        /* Preview Cards Section */
        .preview-section {
            padding: 100px 0;
            background-color: #f5f5f5;
            position: relative;
            overflow: hidden;
        }

        .preview-container {
            display: flex;
            gap: 30px;
            padding: 0 50px;
            overflow-x: auto;
            scroll-snap-type: x mandatory;
            scrollbar-width: none; /* Firefox */
            -ms-overflow-style: none; /* IE and Edge */
        }

        .preview-container::-webkit-scrollbar {
            display: none; /* Chrome, Safari, Opera */
        }

        .preview-card {
            min-width: 300px;
            height: 400px;
            scroll-snap-align: start;
            background-size: cover;
            background-position: center;
            border-radius: 10px;
            display: flex;
            flex-direction: column;
            justify-content: flex-end;
            padding: 30px;
            color: white;
            position: relative;
            overflow: hidden;
            box-shadow: 0 10px 30px rgba(0,0,0,0.3);
            transition: all 0.5s ease;
        }

        .preview-card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(to top, rgba(0,0,0,0.8), transparent);
            z-index: 1;
        }

        .preview-card-content {
            position: relative;
            z-index: 2;
            transform: translateY(50px);
            opacity: 0;
            transition: all 0.5s ease;
        }

        .preview-card:hover .preview-card-content {
            transform: translateY(0);
            opacity: 1;
        }

        .preview-card h3 {
            font-size: 1.5rem;
            margin-bottom: 10px;
        }

        .preview-card p {
            font-size: 1rem;
            margin-bottom: 20px;
        }

        .preview-card .btn {
            display: inline-block;
            padding: 10px 20px;
            background-color: #f0c14b;
            color: #000;
            text-decoration: none;
            border-radius: 5px;
            font-weight: 700;
            transition: all 0.3s ease;
        }

        .preview-card .btn:hover {
            background-color: #e6b800;
        }

        /* About Section */
        .about-section {
            padding: 100px 0;
            background-color: white;
            position: relative;
        }

        .section-title {
            text-align: center;
            margin-bottom: 50px;
            font-size: 2.5rem;
        }

        .about-content {
            display: flex;
            align-items: center;
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
            gap: 50px;
        }

        .about-text {
            flex: 1;
        }

        .about-text h3 {
            font-size: 1.8rem;
            margin-bottom: 20px;
            color: #333;
        }

        .about-text p {
            font-size: 1.1rem;
            line-height: 1.8;
            margin-bottom: 20px;
            color: #666;
        }

        .about-image {
            flex: 1;
            height: 500px;
            background-image: url('https://kushmaartproject.com.np/wp-content/uploads/2023/05/DSC_0003-1-scaled.jpg');
