            background-position: center;
            border-radius: 10px;
            box-shadow: 0 10px 30px rgba(0,0,0,0.1);
        }

        /* Gallery Section */
        .gallery-section {
            padding: 100px 0;
            background-color: #f5f5f5;
        }

        .gallery-container {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
            gap: 20px;
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
        }

        .gallery-item {
            height: 300px;
            overflow: hidden;
            border-radius: 10px;
            position: relative;
            box-shadow: 0 5px 15px rgba(0,0,0,0.1);
            transition: all 0.5s ease;
        }

        .gallery-item img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            transition: all 0.5s ease;
        }

        .gallery-item:hover {
            transform: translateY(-10px);
            box-shadow: 0 15px 30px rgba(0,0,0,0.2);
        }

        .gallery-item:hover img {
            transform: scale(1.1);
        }

        /* Donate Section */
        .donate-section {
            padding: 100px 0;
            background-color: #222;
            color: white;
            text-align: center;
        }

        .donate-container {
            max-width: 800px;
            margin: 0 auto;
            padding: 0 20px;
        }

        .donate-section h2 {
            color: white;
            margin-bottom: 30px;
        }

