            transform: translateY(-5px);
        }

        /* Contact Section */
        .contact-section {
            padding: 100px 0;
            background-color: white;
        }

        .contact-container {
            max-width: 1200px;
            margin: 0 auto;
            padding: 0 20px;
            display: flex;
            gap: 50px;
        }

        .contact-info {
            flex: 1;
        }

        .contact-info h3 {
            font-size: 1.8rem;
            margin-bottom: 30px;
        }

        .contact-details {
            margin-bottom: 40px;
        }

