            align-items: center;
            gap: 10px;
            font-size: 1.1rem;
        }

        .contact-details i {
            color: #f0c14b;
            font-size: 1.2rem;
        }

        .social-links {
            display: flex;
            gap: 20px;
        }

        .social-links a {
            display: flex;
            align-items: center;
            justify-content: center;
            width: 50px;
            height: 50px;
            background-color: #f5f5f5;
            border-radius: 50%;
            color: #333;
            font-size: 1.5rem;
            transition: all 0.3s ease;
