        // Simple parallax effect
        document.addEventListener('scroll', function() {
            const scrollPosition = window.pageYOffset;
            const parallaxLayers = document.querySelectorAll('.parallax-layer-back, .parallax-layer-deep');

            parallaxLayers.forEach(layer => {
                const speed = layer.classList.contains('parallax-layer-deep') ? 0.5 : 0.3;
                const yPos = -scrollPosition * speed;
                layer.style.transform = `translateY(${yPos}px) translateZ(-1px) scale(2)`;
            });
        });

