            color: var(--text);
            overflow-x: hidden;
            line-height: 1.6;
        }

        /* Typography */
        h1, h2, h3, h4 {
            font-weight: 800;
            text-transform: uppercase;
            letter-spacing: 2px;
            line-height: 1.2;
        }

        /* Navigation */
        nav {
            position: fixed;
            top: 0;
            left: 0;
            width: 100%;
            padding: 25px 5%;
            z-index: 1000;
            display: flex;
            justify-content: space-between;
            align-items: center;
            background-color: rgba(255,255,255,0.9);
            backdrop-filter: blur(10px);
            transition: all 0.4s cubic-bezier(0.165, 0.84, 0.44, 1);
            box-shadow: 0 2px 20px rgba(0,0,0,0.05);
        }

        nav.scrolled {
            padding: 15px 5%;
            background-color: rgba(255,255,255,0.98);
        }

        .logo {
            font-size: 1.8rem;
            font-weight: 900;
            color: var(--primary);
            text-decoration: none;
            display: flex;
            align-items: center;
        }

        .logo img {
            height: 40px;
            margin-right: 10px;
        }

        .nav-links {
            display: flex;
            gap: 30px;
        }

        .nav-links a {
            color: var(--primary);
            text-decoration: none;
            font-weight: 600;
            font-size: 0.9rem;
            letter-spacing: 1px;
            transition: all 0.3s ease;
            position: relative;
        }

        .nav-links a::after {
            content: '';
            position: absolute;
            bottom: -5px;
            left: 0;
            width: 0;
            height: 2px;
            background-color: var(--accent);
            transition: width 0.3s ease;
        }

        .nav-links a:hover::after {
            width: 100%;
        }

        .nav-links a:hover {
            color: var(--accent);
        }

        /* Hero Section - Exact Match to Design */
        .hero {
            height: 100vh;
            display: flex;
            flex-direction: column;
            justify-content: center;
            align-items: center;
            text-align: center;
            position: relative;
            overflow: hidden;
            background-color: var(--primary);
            padding: 0 20px;
        }

        .hero-background {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-image: url('https://kushmaartproject.com.np/wp-content/uploads/2023/05/DSC_0003-1-scaled.jpg');
            background-size: cover;
            background-position: center;
            z-index: 1;
            opacity: 0.4;
        }

        .hero-content {
            position: relative;
            z-index: 2;
            max-width: 900px;
            padding: 0 20px;
        }

        .hero h1 {
            font-size: 6rem;
            margin-bottom: 1rem;
            color: white;
            text-shadow: 2px 2px 10px rgba(0,0,0,0.5);
            animation: fadeInUp 1s ease-out;
        }

        .hero h2 {
            font-size: 1.8rem;
            font-weight: 400;
            margin-bottom: 2rem;
            color: white;
            text-shadow: 1px 1px 5px rgba(0,0,0,0.3);
            font-style: italic;
            animation: fadeInUp 1s ease-out 0.2s forwards;
            opacity: 0;
        }

        .hero p {
            font-size: 1.2rem;
            color: rgba(255,255,255,0.9);
            max-width: 700px;
            margin: 0 auto 3rem;
            animation: fadeInUp 1s ease-out 0.4s forwards;
            opacity: 0;
        }

        .scroll-down {
            position: absolute;
            bottom: 30px;
            left: 50%;
            transform: translateX(-50%);
            color: white;
            font-size: 2rem;
            animation: bounce 2s infinite;
            cursor: pointer;
            z-index: 3;
            transition: all 0.3s ease;
        }

        .scroll-down:hover {
            color: var(--accent);
        }

        @keyframes bounce {
            0%, 20%, 50%, 80%, 100% {
                transform: translateY(0) translateX(-50%);
            }
            40% {
                transform: translateY(-20px) translateX(-50%);
            }
            60% {
                transform: translateY(-10px) translateX(-50%);
            }
        }

        /* Preview Cards Section - Parallax Effect */
        .preview-section {
            padding: 100px 5%;
            position: relative;
            background-color: var(--secondary);
            overflow: hidden;
        }

        .preview-title {
            text-align: center;
            margin-bottom: 60px;
            font-size: 2.5rem;
            color: var(--primary);
        }

        .preview-container {
            display: flex;
            gap: 30px;
            padding: 30px 0;
            overflow-x: auto;
            scroll-snap-type: x mandatory;
            scrollbar-width: none;
            -ms-overflow-style: none;
        }

        .preview-container::-webkit-scrollbar {
            display: none;
        }

        .preview-card {
            min-width: 350px;
            height: 500px;
            scroll-snap-align: start;
            background-size: cover;
            background-position: center;
            border-radius: 15px;
            position: relative;
            overflow: hidden;
            box-shadow: 0 15px 40px rgba(0,0,0,0.2);
            transition: all 0.5s cubic-bezier(0.215, 0.61, 0.355, 1);
        }

        .preview-card::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(to top, rgba(0,0,0,0.8) 0%, rgba(0,0,0,0) 50%);
            z-index: 1;
        }

        .preview-card-content {
            position: absolute;
            bottom: 0;
            left: 0;
            width: 100%;
            padding: 40px;
            z-index: 2;
            transform: translateY(100px);
            opacity: 0;
            transition: all 0.6s cubic-bezier(0.215, 0.61, 0.355, 1);
        }

        .preview-card:hover {
            transform: scale(1.03);
        }

        .preview-card:hover .preview-card-content {
            transform: translateY(0);
            opacity: 1;
        }

        .preview-card h3 {
            font-size: 1.8rem;
            margin-bottom: 15px;
            color: white;
        }

        .preview-card p {
            font-size: 1rem;
            margin-bottom: 25px;
            color: rgba(255,255,255,0.9);
        }

        .preview-card .btn {
            display: inline-block;
            padding: 12px 30px;
            background-color: var(--accent);
            color: white;
            text-decoration: none;
            border-radius: 30px;
            font-weight: 700;
            font-size: 0.9rem;
            letter-spacing: 1px;
            transition: all 0.3s ease;
        }

        .preview-card .btn:hover {
            background-color: #c1121f;
            transform: translateY(-3px);
            box-shadow: 0 5px 15px rgba(0,0,0,0.2);
        }

        /* About Section */
        .about-section {
            padding: 120px 5%;
            background-color: white;
            position: relative;
        }

        .section-title {
            text-align: center;
            margin-bottom: 80px;
            font-size: 2.8rem;
            color: var(--primary);
        }

        .about-content {
            display: flex;
            align-items: center;
            max-width: 1200px;
            margin: 0 auto;
            gap: 60px;
        }

        .about-text {
            flex: 1;
        }

        .about-text h3 {
            font-size: 2rem;
            margin-bottom: 30px;
            color: var(--primary);
        }

        .about-text p {
            font-size: 1.1rem;
            line-height: 1.8;
            margin-bottom: 25px;
            color: var(--light-text);
        }

        .about-image {
            flex: 1;
            height: 500px;
            border-radius: 10px;
            box-shadow: 0 20px 40px rgba(0,0,0,0.1);
            overflow: hidden;
            position: relative;
        }

        .about-image img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            transition: transform 0.8s ease;
        }

        .about-image:hover img {
            transform: scale(1.05);
        }

        /* Gallery Section */
        .gallery-section {
            padding: 120px 5%;
            background-color: var(--secondary);
        }

        .gallery-container {
            display: grid;
            grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
            gap: 25px;
            max-width: 1200px;
            margin: 0 auto;
        }

        .gallery-item {
            height: 300px;
            overflow: hidden;
            border-radius: 10px;
            position: relative;
            box-shadow: 0 10px 30px rgba(0,0,0,0.1);
            transition: all 0.5s cubic-bezier(0.215, 0.61, 0.355, 1);
        }

        .gallery-item img {
            width: 100%;
            height: 100%;
            object-fit: cover;
            transition: all 0.8s ease;
        }

        .gallery-item:hover {
            transform: translateY(-10px);
            box-shadow: 0 20px 50px rgba(0,0,0,0.2);
        }

        .gallery-item:hover img {
            transform: scale(1.1);
        }

        /* Donate Section */
        .donate-section {
            padding: 120px 5%;
            background-color: var(--primary);
            color: white;
            text-align: center;
            position: relative;
            overflow: hidden;
        }

        .donate-section::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background-image: url('https://kushmaartproject.com.np/wp-content/uploads/2023/05/DSC_0006-scaled.jpg');
            background-size: cover;
            background-position: center;
            opacity: 0.2;
            z-index: 1;
        }

        .donate-container {
            position: relative;
            z-index: 2;
            max-width: 800px;
            margin: 0 auto;
        }

        .donate-section h2 {
            color: white;
            margin-bottom: 30px;
        }

        .donate-section p {
            font-size: 1.2rem;
            margin-bottom: 40px;
            line-height: 1.8;
            color: rgba(255,255,255,0.9);
        }

        .donate-btn {
            display: inline-block;
            padding: 18px 45px;
            background-color: var(--accent);
            color: white;
            text-decoration: none;
            border-radius: 30px;
            font-weight: 700;
            font-size: 1.1rem;
            letter-spacing: 1px;
            transition: all 0.3s ease;
            margin-bottom: 30px;
            box-shadow: 0 10px 30px rgba(230, 57, 70, 0.3);
        }

        .donate-btn:hover {
            background-color: #c1121f;
            transform: translateY(-5px);
            box-shadow: 0 15px 40px rgba(230, 57, 70, 0.4);
        }

        /* Contact Section */
        .contact-section {
            padding: 120px 5%;
            background-color: white;
        }

        .contact-container {
            display: flex;
            max-width: 1200px;
            margin: 0 auto;
            gap: 60px;
        }

        .contact-info {
            flex: 1;
        }

        .contact-info h3 {
            font-size: 2rem;
            margin-bottom: 40px;
            color: var(--primary);
        }

        .contact-details {
            margin-bottom: 50px;
        }

        .contact-details p {
            margin-bottom: 20px;
            display: flex;
            align-items: center;
            gap: 15px;
            font-size: 1.1rem;
            color: var(--light-text);
        }

        .contact-details i {
            color: var(--accent);
            font-size: 1.3rem;
            width: 30px;
            text-align: center;
        }

        .social-links {
            display: flex;
            gap: 20px;
        }

        .social-links a {
            display: flex;
            align-items: center;
            justify-content: center;
            width: 50px;
            height: 50px;
            background-color: var(--secondary);
            border-radius: 50%;
            color: var(--primary);
            font-size: 1.3rem;
            transition: all 0.3s ease;
        }

        .social-links a:hover {
            background-color: var(--accent);
            color: white;
            transform: translateY(-5px);
        }

        .contact-form {
            flex: 1;
            background-color: var(--secondary);
            padding: 50px;
            border-radius: 15px;
            box-shadow: 0 10px 30px rgba(0,0,0,0.05);
        }

        .form-group {
            margin-bottom: 25px;
        }

        .form-group label {
            display: block;
            margin-bottom: 10px;
            font-weight: 600;
            color: var(--primary);
            font-size: 0.9rem;
            letter-spacing: 1px;
        }

        .form-group input,
        .form-group textarea {
            width: 100%;
            padding: 15px;
            border: 1px solid #ddd;
            border-radius: 8px;
            font-size: 1rem;
            transition: all 0.3s ease;
        }

        .form-group input:focus,
        .form-group textarea:focus {
            border-color: var(--accent);
            outline: none;
            box-shadow: 0 0 0 3px rgba(230, 57, 70, 0.1);
        }

        .form-group textarea {
            height: 150px;
            resize: vertical;
        }

        .submit-btn {
            background-color: var(--accent);
            color: white;
            border: none;
            padding: 16px 40px;
            font-size: 1rem;
            font-weight: 700;
            letter-spacing: 1px;
            border-radius: 30px;
            cursor: pointer;
            transition: all 0.3s ease;
            box-shadow: 0 10px 30px rgba(230, 57, 70, 0.3);
        }

        .submit-btn:hover {
            background-color: #c1121f;
            transform: translateY(-3px);
            box-shadow: 0 15px 40px rgba(230, 57, 70, 0.4);
        }

        /* Footer */
        footer {
            background-color: var(--primary);
            color: white;
            padding: 80px 5% 40px;
        }

        .footer-content {
            max-width: 1200px;
            margin: 0 auto;
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
            gap: 40px;
        }

        .footer-column h4 {
            font-size: 1.2rem;
            margin-bottom: 25px;
            color: white;
        }

        .footer-column p {
            margin-bottom: 20px;
            color: rgba(255,255,255,0.7);
        }

        .footer-links {
            list-style: none;
        }

        .footer-links li {
            margin-bottom: 15px;
        }

        .footer-links a {
            color: rgba(255,255,255,0.7);
            text-decoration: none;
            transition: all 0.3s ease;
        }

        .footer-links a:hover {
            color: white;
            padding-left: 5px;
        }

        .copyright {
            text-align: center;
            margin-top: 60px;
            padding-top: 30px;
            border-top: 1px solid rgba(255,255,255,0.1);
            color: rgba(255,255,255,0.5);
            font-size: 0.9rem;
        }

        /* Animations */
        @keyframes fadeInUp {
            from {
                opacity: 0;
                transform: translateY(30px);
            }
            to {
                opacity: 1;
                transform: translateY(0);
            }
        }

        .animate {
            opacity: 0;
        }

        .animated {
            animation: fadeInUp 1s ease-out forwards;
        }

        .delay-1 {
            animation-delay: 0.2s;
        }

        .delay-2 {
            animation-delay: 0.4s;
        }

        .delay-3 {
            animation-delay: 0.6s;
        }

        /* Responsive Design */
        @media (max-width: 1024px) {
            .about-content,
            .contact-container {
                flex-direction: column;
            }

            .about-image {
                width: 100%;
                margin-top: 40px;
            }

            .contact-form {
                margin-top: 40px;
            }
        }

        @media (max-width: 768px) {
            .hero h1 {
                font-size: 4rem;
            }

            .hero h2 {
                font-size: 1.4rem;
            }

            .nav-links {
                gap: 20px;
            }

            .section-title {
                font-size: 2.2rem;
                margin-bottom: 50px;
            }

            .preview-card {
                min-width: 280px;
                height: 450px;
            }
        }

        @media (max-width: 576px) {
            .hero h1 {
                font-size: 3rem;
            }

            .hero h2 {
                font-size: 1.2rem;
            }

            .nav-links {
                display: none;
            }

            .section-title {
                font-size: 1.8rem;
            }

            .preview-card {
                min-width: 250px;
                height: 400px;
            }

            .contact-form {
                padding: 30px;
            }
        }
    </style>
