            entries.forEach(entry => {
                if (entry.isIntersecting) {
                    entry.target.classList.add('animated');
                }
            });
        }, {
            threshold: 0.1
        });

        animateElements.forEach(element => {
            observer.observe(element);
        });

        // Parallax effect for preview cards
        const previewCards = document.querySelectorAll('.preview-card');

        window.addEventListener('scroll', function() {
            const scrollPosition = window.pageYOffset;
            const previewSection = document.querySelector('.preview-section');
            const sectionTop = previewSection.offsetTop;
            const sectionHeight = previewSection.offsetHeight;

            // Only apply parallax when section is in view
            if (scrollPosition > sectionTop - window.innerHeight && 
                scrollPosition < sectionTop + sectionHeight) {

                const scrollPercent = (scrollPosition - sectionTop) / sectionHeight;

                previewCards.forEach((card, index) => {
                    const speed = 0.2 + (index * 0.05);
                    const yPos = -(scrollPosition - sectionTop) * speed;
                    card.style.transform = `translateY(${yPos}px) scale(${1 - scrollPercent * 0.05})`;
                });
            }
        });

        // Gallery lightbox functionality
        const galleryItems = document.querySelectorAll('.gallery-item');

        galleryItems.forEach(item => {
