Here's the complete CSS file you need to create for all the HTML components. Save this as public/css/styles.css:cssCopy/* ===== Base Styles ===== */
:root {
  /* Light Theme */
  --primary-light: #1a1a1a;
  --secondary-light: #f5f5f5;
  --accent-light: #e63946;
  --text-light: #333;
  --card-light: #ffffff;
  --background-light: #f8f8f8;

  /* Dark Theme */
  --primary-dark: #f5f5f5;
  --secondary-dark: #1a1a1a;
  --accent-dark: #ff6b6b;
  --text-dark: #f0f0f0;
  --card-dark: #2d2d2d;
  --background-dark: #121212;
}

[data-theme="light"] {
  --primary: var(--primary-light);
  --secondary: var(--secondary-light);
  --accent: var(--accent-light);
  --text: var(--text-light);
  --card: var(--card-light);
  --background: var(--background-light);
}

[data-theme="dark"] {
  --primary: var(--primary-dark);
  --secondary: var(--secondary-dark);
  --accent: var(--accent-dark);
  --text: var(--text-dark);
  --card: var(--card-dark);
  --background: var(--background-dark);
}

* {
  margin: 0;
  padding: 0;
  box-sizing: border-box;
  transition: background-color 0.3s, color 0.3s;
}

body {
  font-family: 'Montserrat', sans-serif;
  background-color: var(--background);
  color: var(--text);
  line-height: 1.6;
}

/* ===== Navigation ===== */
nav {
  position: fixed;
  top: 0;
  left: 0;
  width: 100%;
  padding: 20px 5%;
  z-index: 1000;
  display: flex;
  justify-content: space-between;
  align-items: center;
  background-color: rgba(255, 255, 255, 0.9);
  backdrop-filter: blur(10px);
  box-shadow: 0 2px 20px rgba(0, 0, 0, 0.05);
}

[data-theme="dark"] nav {
  background-color: rgba(30, 30, 30, 0.9);
}

.logo {
  font-size: 1.8rem;
  font-weight: 900;
  color: var(--primary);
  text-decoration: none;
  display: flex;
  align-items: center;
}

.logo img {
  height: 40px;
  margin-right: 10px;
}

.nav-links {
  display: flex;
  gap: 30px;
  align-items: center;
}

.nav-links a {
  color: var(--primary);
  text-decoration: none;
  font-weight: 600;
  font-size: 0.9rem;
  letter-spacing: 1px;
  position: relative;
}

.nav-links a::after {
  content: '';
  position: absolute;
  bottom: -5px;
  left: 0;
  width: 0;
  height: 2px;
  background-color: var(--accent);
  transition: width 0.3s ease;
}

.nav-links a:hover::after {
  width: 100%;
}

.theme-toggle, .language-toggle {
  cursor: pointer;
  padding: 8px 12px;
  border-radius: 20px;
  background-color: var(--card);
  display: flex;
  align-items: center;
  gap: 8px;
  font-weight: 600;
}

/* ===== Hero Section ===== */
.hero {
  height: 100vh;
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  text-align: center;
  position: relative;
  overflow: hidden;
  padding: 0 20px;
  background-color: var(--primary-light); /* Dark background regardless of theme */
  color: white;
}

.hero-background {
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background-image: url('../images/hero-bg.jpg');
  background-size: cover;
  background-position: center;
  z-index: 1;
  opacity: 0.4;
}

.hero-content {
  position: relative;
  z-index: 2;
  max-width: 900px;
  padding: 0 20px;
}

.hero h1 {
  font-size: 6rem;
  margin-bottom: 1rem;
  color: white;
  text-shadow: 2px 2px 10px rgba(0, 0, 0, 0.5);
  animation: fadeInUp 1s ease-out;
}

.hero h2 {
  font-size: 1.8rem;
  font-weight: 400;
  margin-bottom: 2rem;
  color: white;
  text-shadow: 1px 1px 5px rgba(0, 0, 0, 0.3);
  font-style: italic;
  animation: fadeInUp 1s ease-out 0.2s forwards;
  opacity: 0;
}

.hero p {
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.9);
  max-width: 700px;
  margin: 0 auto 3rem;
  animation: fadeInUp 1s ease-out 0.4s forwards;
  opacity: 0;
}

.scroll-down {
  position: absolute;
  bottom: 30px;
  left: 50%;
  transform: translateX(-50%);
  color: white;
  font-size: 2rem;
  animation: bounce 2s infinite;
  cursor: pointer;
  z-index: 3;
  transition: all 0.3s ease;
}

.scroll-down:hover {
  color: var(--accent);
}

/* ===== Preview Cards Section ===== */
.preview-section {
  padding: 100px 5%;
  position: relative;
  background-color: var(--secondary);
  overflow: hidden;
}

.preview-title {
  text-align: center;
  margin-bottom: 60px;
  font-size: 2.5rem;
  color: var(--primary);
}

.preview-container {
  display: flex;
  gap: 30px;
  padding: 30px 0;
  overflow-x: auto;
  scroll-snap-type: x mandatory;
  scrollbar-width: none;
}

.preview-container::-webkit-scrollbar {
  display: none;
}

.preview-card {
  min-width: 350px;
  height: 500px;
  scroll-snap-align: start;
  background-size: cover;
  background-position: center;
  border-radius: 15px;
  position: relative;
  overflow: hidden;
  box-shadow: 0 15px 40px rgba(0, 0, 0, 0.2);
  transition: all 0.5s cubic-bezier(0.215, 0.61, 0.355, 1);
}

.preview-card::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(to top, rgba(0, 0, 0, 0.8) 0%, rgba(0, 0, 0, 0) 50%);
  z-index: 1;
}

.preview-card-content {
  position: absolute;
  bottom: 0;
  left: 0;
  width: 100%;
