    transform: translateY(-20px) translateX(-50%);
  }
  60% {
    transform: translateY(-10px) translateX(-50%);
  }
}

.animate {
  opacity: 0;
}

.animated {
  animation: fadeInUp 1s ease-out forwards;
}

.delay-1 {
  animation-delay: 0.2s;
}

.delay-2 {
  animation-delay: 0.4s;
}

.delay-3 {
  animation-delay: 0.6s;
}

/* ===== Responsive Design ===== */
@media (max-width: 1024px) {
  .about-content,
  .contact-container {
    flex-direction: column;
  }

  .about-image {
    width: 100%;
    margin-top: 40px;
  }

  .contact-form {
    margin-top: 40px;
  }
}

@media (max-width: 768px) {
  .hero h1 {
    font-size: 4rem;
  }

  .hero h2 {
    font-size: 1.4rem;
  }

  .nav-links {
    gap: 20px;
  }

  .section-title {
    font-size: 2.2rem;
    margin-bottom: 50px;
  }

  .preview-card {
    min-width: 280px;
    height: 450px;
  }
}

@media (max-width: 576px) {
  .hero h1 {
